import React, { Component } from "react";
import { Menuitems } from "./Menuitems";
import "./Navbar.css";

class Navbar extends Component {
  state = { 
    clicked: false,
    profiles: [
      {
          username: "",
          
          ticketsBought: [
              {
                  _id: "0",
                  club: "no profile loaded",
                  Day: "no profile loaded",
                }
          ]
      },
      {
          username: "Nico",
          
          ticketsBought: [
              {
                  _id: "5b21ca3eeb7f6fbccd471815",
                  club: "ZeroZero",
                  Day: "05/03/2022",
                }
          ]
      },
      {
          username: "Louis Raj Khullar Godtfredsen",
      
          ticketsBought: [
              {
                  _id: "5b21ca3eeb7f6fbccd471815",
                  club: "Second Bridge",
                  Day: "06/03/2022",
                }
          ]
      }
  ]
  };
  constructor(props) {
    super(props)
    this.addTicketToProfile= this.addTicketToProfile.bind(this)
  }
  addTicketToProfile(ticket, name) {
    let index= this.state.profiles.findIndex(x => x.username === name) ;
    this.setState({profiles: this.state.profiles[index].ticketsBought.push({_id: ticket._id, club: ticket.club, Day: ticket.Day}) });
       
}
  
  handleClick = () => {
    this.setState({ clicked: !this.state.clicked });
  };

  render() {
    return (
      <nav className="NavbarItems">
        <h1 className="app-Logo">
          Clicket<i class="fa-solid fa-wave-square"></i>
        </h1>

        <ul className={this.state.clicked ? "nav-menu active" : "nav-menu"}>
          {Menuitems.map((item, index) => {
            return (
              <li key={index}>
                <a className={item.cName} href={item.url} key = {index} profiles = {this.state.profiles} >
                  {item.title}
                </a>
              </li>
            );
          })}
        </ul>
      </nav>
    );
  }
}

export default Navbar;
