import React, { useState } from "react";
import LoginForm from "./components/LoginForm";
import Navbar from "./components/Navbar/Navbar";
import Clubs from "./components/clubs";
import Banner from "./components/Banner";
import {updateName} from "./ProfilesDB"
import raw from './usernames.txt';

import {
  BrowserRouter as Router,
  Route,
  Routes,
} from "react-router-dom";
import { MarketPlace } from "./MarketPlace";
import { Profile } from "./Profile";


function App() {
  

  
  const adminUser = {
    email: "louis.godtfredsen@gmail.com",
    password: "123", 
  };



  const [user, setUser] = useState({ name: "", email: "" });
  const [error, setError] = useState("");
  const[loggedin, setLoggedin] = useState({loggedin:false});
  const [profiles, setProfile] = useState([
    {
        username: "",
        
        ticketsBought: [
            {
                _id: "0",
                club: "no profile loaded",
                Day: "no profile loaded",
              }
        ]
    },
    {
        username: "Nico",
        
        ticketsBought: [
            {
                _id: "5b21ca3eeb7f6fbccd471815",
                club: "ZeroZero",
                Day: "05/03/2022",
              }
        ]
    },
    {
        username: "Louis Raj Khullar Godtfredsen",
    
        ticketsBought: [
            {
                _id: "5b21ca3eeb7f6fbccd471815",
                club: "Second Bridge",
                Day: "06/03/2022",
              }
        ]
    }
  ]);
  
  const Login = (details) => {
    
    Clubs.username = details.name;
    localStorage.setItem("name", details.name);
  
    if (
      details.email === adminUser.email &&
      details.password === adminUser.password
    ) {
      console.log("Logged in");

      setUser({
        name: details.name,
        email: details.email,
      });

      setLoggedin({
        loggedin: false
      });
    } else {
      console.log("Details do not match");
      setError("Details do not match");
    }
  };

  const Logout = () => {
    setUser({ name: "", email: "" });
  };

  const displayLogin = () => {
   

      return(

        
        <div className='Login'>
          {(loggedin) ? (
            (user.email !== "") ? (
              <div className='welcome'>
                

                <button onClick ={Logout}>Logout</button>
              </div>
            ): (
              <LoginForm Login={Login} error={error}></LoginForm>
            )
          ) : (
            null
          )}
                    
                </div>

      )

  }

  return (
      <div className="App">
        <Navbar add = {setProfile} profiles = {profiles}></Navbar>

        <Router add = {setProfile} profiles = {profiles}>
        
            <Routes add = {setProfile} profiles = {profiles}>
            
              <Route path="/chats" element={<div>Hello this is the Chat</div>} />
              <Route path="/profile" element={<Profile add = {setProfile} profiles = {profiles} />}/>

              <Route path="/MarketPlace" element={<MarketPlace add = {setProfile} profiles = {profiles}></MarketPlace>}/>

              <Route path="/" element={
              
                (user.email !== "") ? (
                  <MarketPlace add = {setProfile} profiles = {profiles}></MarketPlace>

                ): (
                  <div className="Login">
                  <LoginForm Login={Login} error={error} add = {setProfile} profiles = {profiles}></LoginForm>
                  </div>
                )}
                
                />

                
          </Routes>

       </Router>
      </div>
        
  );
}
export default App;
