import React, {useState} from "react";

function LoginForm({Login, error}){

    const [details,setDetails] = useState({name:"",email:"", password:""});

    const submitHandler = e => {
        e.preventDefault();
        Login(details);
    }

    return(
        <form onSubmit={submitHandler}>
            <div className="form-inner">
                <h2>Login</h2>
                {/*If there is an error, display error message else display nothing*/}
                {(error !== "") ? (<div className="error">{error}</div>) : ""}


                {/*This is the actual form, each one contains its label and input*/}
                <div className="form-group">
                    <label htmlFor="name">Name:</label>
                    <input type="text" name = "name" id = "name" onChange = {e => setDetails({...details,name:e.target.value})} value={details.name}/>
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input type="email" name = "email" id="email" onChange = {e => setDetails({...details,email:e.target.value})} value={details.email}/>
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password:</label>
                    <input type="password" name="password" id="password" onChange = {e => setDetails({...details,password:e.target.value})} value={details.password}/>
                </div>

                <input type="submit" value="LOGIN"></input>

                <p>Don't have an account? Sign up <a href = "https://www.youtube.com/watch?v=2dbR2JZmlWo">here!</a>!</p>
            </div>
        </form>
    )
}

export default LoginForm;
