import React, { useState } from "react";
import Clubs from "./clubs";

function LoginForm({ Login, error }) {
  
  
  const [details, setDetails] = useState({ name: "", email: "", password: "" });

  const submitHandler = (e) => {
    e.preventDefault();
    Login(details);
  };
  
  
  function handleChange(input) {
    setDetails({ ...details, name: input.target.value })
    console.log("this" + details.name)
    Clubs.setUserName(details.name);
  }

  return (
    
    <form onSubmit={submitHandler}>
      <div className="form-inner">
        <h2>Login</h2>
        {/*If there is an error, display error message else display nothing*/}
        {error !== "" ? <div className="error">{error}</div> : ""}

        {/*This is the actual form, each one contains its label and input*/}
        <div className="form-group">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            name="name"
            id="name"
            onChange={(e) => handleChange(e)}
            value={details.name}
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            name="email"
            id="email"
            onChange={(e) => setDetails({ ...details, email: e.target.value })}
            value={details.email}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password:</label>
          <input
            type="password"
            name="password"
            id="password"
            onChange={(e) =>
              setDetails({ ...details, password: e.target.value })
            }
            value={details.password}
          />
        </div>

        <input type="submit" value="LOGIN"></input>

        <p>
          Don't have an account? Sign up{" "}
          <a href="https://www.youtube.com/watch?v=2dbR2JZmlWo">here!</a>!
        </p>
      </div>
    </form>
  );
  
}


export default LoginForm;
