import React, { useState } from "react";
import LoginForm from "./components/LoginForm";
import Navbar from "./components/Navbar/Navbar";
import Clubs from "./components/clubs";
import Banner from "./components/Banner";
import {updateName} from "./ProfilesDB"
import raw from './usernames.txt';
import { getZero  } from "./Zero";
import { getSecondBridge} from "./SecondBridge";
import { getLabyrinth } from "./Labyrinth";
import { getScore} from "./Score";

import {
  BrowserRouter as Router,
  Route,
  Routes,
} from "react-router-dom";
import { MarketPlace } from "./MarketPlace";
import { Profile } from "./Profile";


function App() {
  

  
  const adminUser = {
    email: "louis.godtfredsen@gmail.com",
    password: "123", 
  };

  
  const [clubs,setclub]= useState([[{_id: 0, club: "no selection", seller: {_id: 0, name: "no selection"}, UserRating: 0, Day:"no selection", Price: "0"}],getZero(), getSecondBridge(),getLabyrinth(), getScore()]);
    
  const [user, setUser] = useState({ name: "", email: "" });
  const [error, setError] = useState("");
  const[loggedin, setLoggedin] = useState({loggedin:false});
  const [profiles, setProfile] = useState([
    {
        username: "",
        
        ticketsBought: [
            {
                _id: "0",
                club: "no profile loaded",
                Day: "no profile loaded",
              }
        ]
    },
    {
        username: "Nico",
        
        ticketsBought: [
            {
                _id: "5b21ca3eeb7f6fbccd471815",
                club: "ZeroZero",
                Day: "30/04/2022",
              },
              {
                _id: "5b21ca3eeb7f6fbccd471822",
                club: "Labyrinth",
                Day: "09/04/2022",
              }, 
              {
                _id: "5b21ca3eeb7f6fbccd471823",
                club: "Score",
                Day: "11/04/2022",
              }
        ]
    },
    {
      username: "Awsat",
      
      ticketsBought: [
          {
              _id: "5b21ca3eeb7f6fbccd471816",
              club: "Score",
              Day: "15/04/2022",
            },
            {
              _id: "5b21ca3eeb7f6fbccd471824",
              club: "Second Bridge",
              Day: "16/04/2022",
            }
      ]
  },
  {
    username: "Dylan",
    
    ticketsBought: [
        {
            _id: "5b21ca3eeb7f6fbccd471817",
            club: "Labyrinth",
            Day: "10/04/2022",
          },
          {
            _id: "5b21ca3eeb7f6fbccd471830",
            club: "Score",
            Day: "17/04/2022",
          }
    ]
},
{
  username: "Elea",
  
  ticketsBought: [
      {
          _id: "5b21ca3eeb7f6fbccd471818",
          club: "ZeroZero",
          Day: "11/04/2022",
        }
  ]
},
{
  username: "Joe",
  
  ticketsBought: [
      {
          _id: "5b21ca3eeb7f6fbccd471819",
          club: "Second Bridge",
          Day: "14/04/2022",
        },
        {
          _id: "5b21ca3eeb7f6fbccd471843",
          club: "Score",
          Day: "17/04/2022",
        }
  ]
},
{
  username: "Fabio",
  
  ticketsBought: [
      {
          _id: "5b21ca3eeb7f6fbccd471820",
          club: "ZeroZero",
          Day: "15/03/2022",
        }
  ]
},
    {
        username: "Louis",
    
        ticketsBought: [
            {
                _id: "5b21ca3eeb7f6fbccd471821",
                club: "Second Bridge",
                Day: "06/03/2022",
              },
              {
                _id: "5b21ca3eeb7f6fbccd471835",
                club: "Second Bridge",
                Day: "10/03/2022",
              }
        ]
    }

  ]);
  
  const Login = (details) => {
    localStorage.setItem("clubs", JSON.stringify(clubs));
    localStorage.setItem("prof", JSON.stringify(profiles));
    console.log(JSON.parse(localStorage.getItem("clubs")));
    Clubs.username = details.name;
    localStorage.setItem("name", details.name);
  
    if (
      details.email === adminUser.email &&
      details.password === adminUser.password
    ) {
      console.log("Logged in");

      setUser({
        name: details.name,
        email: details.email,
      });

      setLoggedin({
        loggedin: false
      });
    } else {
      console.log("Details do not match");
      setError("Details do not match");
    }
  };

  const Logout = () => {
    setUser({ name: "", email: "" });
  };

  const displayLogin = () => {
   

      return(

        
        <div className='Login'>
          {(loggedin) ? (
            (user.email !== "") ? (
              <div className='welcome'>
                

                <button onClick ={Logout}>Logout</button>
              </div>
            ): (
              <LoginForm Login={Login} error={error}></LoginForm>
            )
          ) : (
            null
          )}
                    
                </div>

      )

  }

  return (
      <div className="App">
        <Navbar setProfile = {setProfile} profiles = {profiles}></Navbar>

        <Router setProfile= {setProfile} profiles = {profiles}>
        
            <Routes setProfile = {setProfile} profiles = {profiles}>
            
              <Route path="/chats" element={<div>Hello this is the Chat</div>} />
              <Route path="/profile" element={<Profile setProfile = {setProfile} profiles = {profiles} />}/>

              <Route path="/MarketPlace" element={<MarketPlace setProfile = {setProfile} profiles = {profiles}></MarketPlace>}/>

              <Route path="/" element={
              
                (user.email !== "") ? (
                  <MarketPlace setProfile = {setProfile} profiles = {profiles}></MarketPlace>

                ): (
                  <div className="Login">
                  <LoginForm Login={Login} error={error} setProfile = {setProfile} profiles = {profiles}></LoginForm>
                  </div>
                )}
                
                />

                
          </Routes>

       </Router>
      </div>
        
  );
}
export default App;
