import React, { Component } from 'react'
import logo from '../components/public/image/biopicimg.jpg';
import cse from '../components/public/image/yt1.png';
import raw from "../usernames.txt"
import Clubs from './clubs';
import { getName } from "../ProfilesDB";

class Banner extends Component {
    state = {
        change: false,
        time: 0,
        profiles: [
            {
                username: "",
                
                ticketsBought: [
                    {
                        _id: "0",
                        club: "no profile loaded",
                        Day: "no profile loaded",
                      }
                ]
            },
            {
                username: "Nico",
                
                ticketsBought: [
                    {
                        _id: "5b21ca3eeb7f6fbccd471815",
                        club: "ZeroZero",
                        Day: "05/03/2022",
                      }
                ]
            },
            {
                username: "Louis Raj Khullar Godtfredsen",
            
                ticketsBought: [
                    {
                        _id: "5b21ca3eeb7f6fbccd471815",
                        club: "Second Bridge",
                        Day: "06/03/2022",
                      }
                ]
            }
        ]
    
    }
    constructor(props){
        super(props)
        this.addTicketToProfile = this.addTicketToProfile.bind(this);
    }

    
    static addTicketToProfile(ticket, name) {
        let index= this.state.profiles.findIndex(x => x.username === name) ;
        this.setState({profiles: this.state.profiles[index].ticketsBought.push({_id: ticket._id, club: ticket.club, Day: ticket.Day}) });
        console.log("ticket added");
        
    }
    handleChange = () =>{
    
        console.log(localStorage.getItem("name") + " he");
        this.setState({change: !this.state.change})
    }
    render() {
        return (
            <div>
                <section className="container-banner">

                <a href="#home"><img id="profilepic" src={logo}  width="170" height="170" alt="profilepic"/></a>
                <h1> Joe Pohlman </h1>
                <h5> Rating: 4.5 </h5>
                <p id="paragarph1"> joe.pohlman23@gmail.com <br/>
                Instagram: @joepoh
                <br/> <br/> Location: Bath </p>
                <button>Change details</button>
                {/* <a id="logo" href="https://www.contentstack.com/" ><img src={cse} width="30" height="30" alt="cselogo"/></a>  */}

                </section>
                <button onClick = {() => this.handleChange()}>view tickets</button>
                <table className="table">
                <thead className="table-head">
                <tr className="table-columns">
                    <th className="table-column">Club</th>
                    <th className="table-column">Date</th>
                </tr>
                </thead>
                <tbody className="all-table-content">
                {this.state.profiles[this.state.profiles.findIndex(x => x.username === Clubs.username)].ticketsBought.map((ticket) => (
                    <tr>
                    <td className="table-content">{ticket.club}</td>
                    <td className="table-content">{ticket.Day}</td>
            
                    </tr>
                ))}
                </tbody>
      </table>
                
            </div>

        )
    }
}

export default Banner
